using UnityEngine;
using System.Collections;
using System.Net.Sockets;
using System.Threading;
using System.Collections.Generic;
using UnityEngine.UI;
using System.IO;
using System;
using UnityEngine.Networking;
using System.Net;

namespace UniWebServer
{


    public class EmbeddedWebServerComponent : MonoBehaviour
    {
        public bool startOnAwake = true;
        public int port = 8079;
        public int workerThreads = 2;
        public bool processRequestsInMainThread = true;
        public bool logRequests = true;
        
        private int requestCount =0;

        WebServer server;
        private String sampleImage = "<img src=\"data:image/jpeg;base64,\" alt=\"\" />";


        void Start ()
        {

            Screen.sleepTimeout = SleepTimeout.NeverSleep;

            if (processRequestsInMainThread)
                Application.runInBackground = true;
            server = new WebServer (port, workerThreads, processRequestsInMainThread);
            server.logRequests = logRequests;
            server.HandleRequest += HandleRequest;
            if (startOnAwake) {
                server.Start ();
            }
 
            GameObject.Find("serveraddress").GetComponent<Text>().text = GetLocalIPAddress() + ":" + port;
        }

        public static string GetLocalIPAddress()
        {
            var host = Dns.GetHostEntry(Dns.GetHostName());
            foreach (var ip in host.AddressList)
            {
                Debug.Log("found network ip: "+ ip.ToString());
                if (ip.AddressFamily == AddressFamily.InterNetwork && ip.ToString()!="127.0.0.1")
                {
                    return ip.ToString();
                }
            }
            return "No network adapters found";

        }


        void OnApplicationQuit ()
        {
            server.Dispose ();
        }

        void Update ()
        {
            if (server.processRequestsInMainThread) {
                server.ProcessRequests ();    
            }
        }


        void HandleRequest (Request request, Response response)
        {
            requestCount++;
            GameObject.Find("counter").GetComponent<Text>().text = requestCount+"";
            GameObject.Find("accesslog").GetComponent<Text>().text = DateTime.Now.ToString();


            response.statusCode = 200;
            response.message = "OK";
            response.Write(sampleImage);



            /*
            if (resources.ContainsKey (request.uri.LocalPath)) {
                try {
                    resources [request.uri.LocalPath].HandleRequest (request, response);
                } catch (Exception e) {
                    response.statusCode = 500;
                    response.Write (e.Message);
                }
            } else {
                response.statusCode = 404;
                response.message = "Not Found.";
                response.Write (request.uri.LocalPath + " not found.");
            }*/
        }

    }


}